        let event2 = Event {
            kind: EventKind::Modify(notify::event::ModifyKind::Data(
                notify::event::DataChange::Content,
            )),
            paths: vec![other_file],
            attrs: Default::default(),
        };

        assert!(!ConfigFileWatcher::is_config_file_event(
            &event2,
            &config_file
        ));
    }
}
