      }
      if (command === 'get_session_content') {
        return 'content'
      }
      throw new Error(`Unexpected command ${command}`)
    })

    const deferred = (() => {
      let resolve: () => void
      const promise = new Promise<void>(res => {
        resolve = res
      })
      return { promise, resolve: resolve! }
    })()

    processSession.mockImplementation(() => deferred.promise)

    const { result } = renderHook(() => useBackgroundProcessing())

    let firstCall: Promise<void>
    await act(async () => {
      firstCall = result.current.processNow()
      await Promise.resolve()
    })
    await waitFor(() => expect(processSession).toHaveBeenCalledTimes(1))

    await act(async () => {
      await result.current.processNow()
    })
    expect(processSession).toHaveBeenCalledTimes(1)

    await act(async () => {
      deferred.resolve()
      await Promise.resolve()
    })

    await firstCall!
    await waitFor(() => expect(result.current.isProcessing).toBe(false))
  })
